mapper.select:
SELECT * FROM Notifications n 
LEFT JOIN Appointments a ON n.appointment_id = a.id 
WHERE a.client_id = #{clientId}

SELECT * FROM Notifications n 
LEFT JOIN Appointments a ON n.appointment_id = a.id 
WHERE a.employee_id = #{employeeId}

mapper.update:
UPDATE Notifications 
SET message = #{message}, 
    appointment_id = #{appointmentId}, 
    status = #{status} 
WHERE id = #{id}
