mapper.select:
SELECT * FROM Offerings o 
LEFT JOIN Employees e ON o.employee_id = e.id 
WHERE e.business_id = #{businessId}

SELECT * FROM Offerings 
WHERE employee_id = #{employeeId} 
AND procedure_id = #{procedureId}

mapper.update:
UPDATE Offerings 
SET price = #{price} 
WHERE employee_id = #{employeeId} 
AND procedure_id = #{procedureId}

mapper.delete:
DELETE FROM Offerings 
WHERE employee_id = #{employeeId} 
AND procedure_id = #{procedureId}
