mapper.select:
SELECT * FROM Reviews r 
LEFT JOIN Appointments a ON r.appointment_id = a.id 
WHERE a.client_id = #{clientId}

SELECT * FROM Reviews r 
LEFT JOIN Appointments a ON r.appointment_id = a.id 
WHERE a.employee_id = #{employeeId}

mapper.update:
UPDATE Reviews 
SET rating = #{rating}, 
    comment = #{comment} 
WHERE id = #{id}
